import styled from 'styled-components';

export const Title = styled.h1`
	font-size: 2.5em;
	text-align: center;
	color: #000;
`;

// background-color: lightseagreen;
export const Wrapper = styled.section`
	color: white;
	position: relative;
	text-align: center;
`;
export const Container = styled.div`
	width: 600px;
	margin: 3rem auto;
	height: 600px;
	background-color: black;
`;
export const Circle = styled.div`
	border-radius: 50%;
	width: 500px;
	height: 500px;
	background: green;
	:hover {
		background: #66ff00;
	}
	margin: auto;
	position: relative;
	top: 40px;
`;
