import React from 'react';
import { Center, Container2 } from './styled';
const Timer = () => {
	const [time, setTime] = React.useState(0);
	const [timerOn, setTimerOn] = React.useState(false);

	React.useEffect(() => {
		let interval = null;

		if (timerOn) {
			interval = setInterval(() => {
				setTime((prevTime) => prevTime + 10);
			}, 10);
		} else if (!timerOn) {
			clearInterval(interval);
		}

		return () => clearInterval(interval);
	}, [timerOn]);

	return (
		<div>
			<h2>Cronometro</h2>

			<Container2>
				<Center>
					<table>
						<tr>
							<td>
								<h3>Minutos</h3>
							</td>

							<td>
								{' '}
								<h3>Segundos</h3>
							</td>

							<td>
								<h3>Milisegundos</h3>
							</td>
						</tr>
						<tr>
							<td>
								<span>{('0' + Math.floor((time / 60000) % 60)).slice(-2)}</span>
							</td>

							<td>
								<span>{('0' + Math.floor((time / 1000) % 60)).slice(-2)}</span>
							</td>

							<td>
								<span>{('0' + ((time / 10) % 100)).slice(-2)}</span>
							</td>
						</tr>
					</table>
				</Center>

				{!timerOn && time === 0 && (
					<button onClick={() => setTimerOn(true)}>Comenzar</button>
				)}
				{timerOn && <button onClick={() => setTimerOn(false)}>Frenar</button>}
				{!timerOn && time > 0 && (
					<button onClick={() => setTime(0)}>Resetar</button>
				)}
				{!timerOn && time > 0 && (
					<button onClick={() => setTimerOn(true)}>Resumir</button>
				)}
			</Container2>
		</div>
	);
};

export default Timer;
